import React from "react";
import "./hoodies.css";

export default class Backpack extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      cart: [], // Giỏ hàng
      searchQuery: "", // Từ khóa tìm kiếm
    };
  }

  // Hàm thêm sản phẩm vào giỏ hàng
  addToCart = (product) => {
    this.setState((prevState) => {
      const isProductInCart = prevState.cart.some((item) => item.id === product.id);
      if (!isProductInCart) {
        return { cart: [...prevState.cart, { ...product, quantity: 1 }] };
      } else {
        alert("Sản phẩm đã có trong giỏ hàng!");
        return prevState;
      }
    });
  };

  // Hàm mua ngay
  buyNow = (product) => {
    alert(`Bạn đã chọn mua: ${product.name}`);
    this.addToCart(product);
  };

  // Hàm xử lý thanh toán
  handleCheckout = () => {
    if (this.state.cart.length === 0) {
      alert("Giỏ hàng của bạn đang trống! Vui lòng thêm sản phẩm trước khi thanh toán.");
      return;
    }

    const totalPrice = this.getTotalPrice();
    alert(`Thanh toán thành công! Tổng tiền: ${totalPrice.toLocaleString()}đ`);
    this.setState({ cart: [] });
  };

  // Hàm tính tổng tiền
  getTotalPrice = () => {
    return this.state.cart.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  // Hàm tăng số lượng sản phẩm
  increaseQuantity = (productId) => {
    this.setState((prevState) => {
      const newCart = prevState.cart.map((item) =>
        item.id === productId ? { ...item, quantity: item.quantity + 1 } : item
      );
      return { cart: newCart };
    });
  };

  // Hàm giảm số lượng sản phẩm
  decreaseQuantity = (productId) => {
    this.setState((prevState) => {
      const newCart = prevState.cart.map((item) =>
        item.id === productId && item.quantity > 1
          ? { ...item, quantity: item.quantity - 1 }
          : item
      );
      return { cart: newCart };
    });
  };

  // Hàm xóa sản phẩm khỏi giỏ hàng
  removeItemFromCart = (productId) => {
    this.setState((prevState) => {
      const newCart = prevState.cart.filter((item) => item.id !== productId);
      return { cart: newCart };
    });
  };

  // Hàm xử lý thay đổi từ khóa tìm kiếm
  handleSearchChange = (event) => {
    this.setState({ searchQuery: event.target.value });
  };

  render() {
    const hoodies = [
      {
        id: 1,
        name: "Áo Hoodie Thể Thao",
        price: 450000,
        image: "data:image/jpeg;base64,",
        description: "Áo hoodie thể thao, thoải mái, phù hợp với mọi hoạt động ngoài trời.",
      },
      {
        id: 2,
        name: "Áo Hoodie Phong Cách",
        price: 400000,
        image: "data:image/jpeg;base64,",
        description: "Áo hoodie phong cách, chất liệu co giãn, dễ dàng phối đồ.",
      },
      {
        id: 3,
        name: "Áo Hoodie Cổ Chữ V",
        price: 380000,
        image: "data:image/jpeg;base64,",
        description: "Áo hoodie cổ chữ V, phong cách hiện đại, rất thoải mái khi mặc.",
      },
      {
        id: 4,
        name: "Áo Hoodie Cao Cấp",
        price: 550000,
        image: "data:image/jpeg;base64,",
        description: "Áo hoodie cao cấp, chất liệu vải dày dặn, giữ ấm tốt trong mùa đông.",
      },
      {
        id: 5,
        name: "Áo Hoodie luxury Cao Cấp",
        price: 550000,
        image: "data:image/jpeg;base64,",
        description: "Áo hoodie cao cấp, chất liệu vải dày dặn, giữ ấm tốt trong mùa đông.",
      },
      {
        id: 6,
        name: "Áo Hoodie vãi nhung Cao Cấp",
        price: 550000,
        image: "data:image/jpeg;base64,",
        description: "Áo hoodie cao cấp, chất liệu vải dày dặn, giữ ấm tốt trong mùa đông.",
      },
      {
        id: 7,
        name: "Áo Hoodie ấm Cao Cấp",
        price: 550000,
        image: "data:image/jpeg;base64,",
        description: "Áo hoodie cao cấp, chất liệu vải dày dặn, giữ ấm tốt trong mùa đông.",
      },  
      {
        id: 8,
        name: "Áo Hoodie nike Cao Cấp",
        price: 550000,
        image: "data:image/jpeg;base64,",
        description: "Áo hoodie cao cấp, chất liệu vải dày dặn, giữ ấm tốt trong mùa đông.",
      },
      {
        id: 9,
        name: "Áo Hoodie luxut Cao Cấp",
        price: 550000,
        image: "data:image/jpeg;base64,",
        description: "Áo hoodie cao cấp, chất liệu vải dày dặn, giữ ấm tốt trong mùa đông.",
      },
      {
        id: 10,
        name: "Áo Hoodie maybach Cao Cấp",
        price: 550000,
        image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSXnEWpy9ZERtazJ6Yd8px3sBbdYbQ7e_oFng&s",
        description: "Áo hoodie cao cấp, chất liệu vải dày dặn, giữ ấm tốt trong mùa đông.",
      },
    ];

    // Lọc các sản phẩm theo từ khóa tìm kiếm
    const filteredHoodies = hoodies.filter((hoodie) =>
      hoodie.name.toLowerCase().includes(this.state.searchQuery.toLowerCase()) ||
      hoodie.description.toLowerCase().includes(this.state.searchQuery.toLowerCase())
    );

    return (
      <div>
        <div className="search-bar">
          <input
            type="text"
            placeholder="Tìm kiếm sản phẩm..."
            value={this.state.searchQuery}
            onChange={this.handleSearchChange}
          />
        </div>

        <div className="danhsach">
          <center>
            <h1>Danh sách Áo Hoodie</h1>
          </center>
        </div>
        <div className="wrap" style={{ display: "flex", gap: "20px", flexWrap: "wrap" }}>
          {filteredHoodies.length > 0 ? (
            filteredHoodies.map((hoodie) => (
              <div
                key={hoodie.id}
                style={{
                  border: "1px solid #ccc",
                  padding: "20px",
                  borderRadius: "5px",
                  textAlign: "center",
                  width: "200px",
                }}
              >
                <img
                  src={hoodie.image}
                  alt={hoodie.name}
                  style={{ width: "100%", borderRadius: "5px" }}
                />
                <h2>{hoodie.name}</h2>
                <p>{hoodie.description}</p>
                <p style={{ fontWeight: "bold" }}>{hoodie.price.toLocaleString()}đ</p>
                <div style={{ display: "flex", justifyContent: "space-between", gap: "10px" }}>
                  <button
                    style={{
                      padding: "10px 15px",
                      backgroundColor: "#007bff",
                      color: "white",
                      border: "none",
                      borderRadius: "5px",
                      cursor: "pointer",
                    }}
                    onClick={() => this.addToCart(hoodie)}
                  >
                    Thêm vào giỏ hàng
                  </button>
                  <button
                    style={{
                      padding: "10px 15px",
                      backgroundColor: "#28a745",
                      color: "white",
                      border: "none",
                      borderRadius: "5px",
                      cursor: "pointer",
                    }}
                    onClick={() => this.buyNow(hoodie)}
                  >
                    Mua ngay
                  </button>
                </div>
              </div>
            ))
          ) : (
            <p>Không tìm thấy sản phẩm nào.</p>
          )}
        </div>

        {/* Hiển thị giỏ hàng */}
        <div className="cart">
          <h2>Giỏ hàng</h2>
          {this.state.cart.length > 0 ? (
            <div>
              <ul>
                {this.state.cart.map((item) => (
                  <li key={item.id}>
                    <div>
                      <button onClick={() => this.decreaseQuantity(item.id)}>-</button>
                      <span>{item.quantity}</span>
                      <button onClick={() => this.increaseQuantity(item.id)}>+</button>
                    </div>
                    {item.name} - {item.price.toLocaleString()}đ
                    <button
                      onClick={() => this.removeItemFromCart(item.id)}
                      style={{
                        backgroundColor: "red",
                        color: "white",
                        border: "none",
                        borderRadius: "5px",
                        padding: "5px 10px",
                        cursor: "pointer",
                      }}
                    >
                      Xóa
                    </button>
                  </li>
                ))}
              </ul>
              <p style={{ fontWeight: "bold" }}>
                Tổng tiền: {this.getTotalPrice().toLocaleString()}đ
              </p>
              <button
                style={{
                  padding: "10px 15px",
                  backgroundColor: "#ffc107",
                  color: "black",
                  border: "none",
                  borderRadius: "5px",
                  cursor: "pointer",
                }}
                onClick={this.handleCheckout}
              >
                Thanh toán
              </button>
            </div>
          ) : (
            <p>Giỏ hàng của bạn đang trống.</p>
          )}
        </div>
      </div>
    );
  }
}
